mapper.select:
select sii.IDC_NAME as TRANS_IDC_ID,ssw.WEBSITE_NAME as TRANS_WEB_CASE_RID, case WHEN ssp.RID is not null and ssp.RID!='' then '查看明细' else '无' end as TRANS_RID, from SMMS_EVENT_MAIN as ssp left join smms_idc_info as sii on ssp.ACCESS_ID=sii.IDC_ID left join SMMS_WEB_CASE as ssw on ssp.WEB_CASE_RID=ssw.RID
ssp.RECORDSTATE='0'
${whereCondition}
order by ${order}

select ssi.ACCESS_IP as IP, ssp.DOMAIN_NAME as DOMAIN from SMMS_EVENT_MAIN as ssp left JOIN smms_web_case as swc on ssp.WEB_CASE_RID=swc.rid left join smms_web_case_ip as ssi on swc.rid=ssi.case_rid
ssp.RECTIFY_STATE='000' and ssp.IS_FORCE_CLOSE='1' and ssp.RECORDSTATE='0'
${ridCondition}

mapper.update:
update SMMS_EVENT_MAIN set 
RECTIFY_MEASURE=#{RECTIFY_MEASURE},
RECTIFY_STATE=#{RECTIFY_STATE},
FEEDBACK_TIME=#{FEEDBACK_TIME},
FEEDBACK_TIMESTAMP=#{FEEDBACK_TIMESTAMP}
where RID=#{RID}

UPDATE SMMS_EVENT_MAIN

		     RECTIFY_STATE=#{RECTIFY_STATE}, 
		     SEND_TIME=#{SEND_TIME}, 
		     SEND_TIMESTAMP=#{SEND_TIMESTAMP} 

WHERE RID =#{EVENT_ID} and RECTIFY_STATE='000' and IS_FORCE_CLOSE='2' ;


update SMMS_EVENT_MAIN   

	     RECTIFY_STATE=#{RECTIFY_STATE}, 
	     SEND_TIME=#{SEND_TIME}, 
	     SEND_TIMESTAMP=#{SEND_TIMESTAMP} 

where RID=#{EVENT_RID} and RECTIFY_STATE='000' and IS_FORCE_CLOSE='1'and RECORDSTATE='0';


update SMMS_EVENT_MAIN   

	     RECTIFY_STATE=#{RECTIFY_STATE}, 
	     SEND_TIME=#{SEND_TIME}, 
	     SEND_TIMESTAMP=#{SEND_TIMESTAMP} 

where RID=#{EVENT_RID} and RECTIFY_STATE='000' and IS_FORCE_CLOSE='1'and RECORDSTATE='0';

mapper.select:
select count(*) NUM from SMMS_EVENT_MAIN as ssp left join smms_idc_info as sii on ssp.ACCESS_ID=sii.IDC_ID
ssp.RECORDSTATE='0'
${whereCondition}

