update SMMS_EVENT_MAIN
EVENT_SOURCE=#{EVENT_SOURCE}, CLIENTNAME=#{CLIENTNAME}, REPORT_CITY=#{REPORT_CITY}, EVENT_TYPE1=#{EVENT_TYPE1}, THREAT_LEVEL=#{THREAT_LEVEL}, THREAT_NAME=#{THREAT_NAME}, THREAT_TYPE1=#{THREAT_TYPE1}, THREAT_TYPE2=#{THREAT_TYPE2}, THREAT_TYPE3=#{THREAT_TYPE3}, THREAT_TYPE4=#{THREAT_TYPE4}, ACCESS_ID=#{ACCESS_ID}, ROOM_NAME=#{ROOM_NAME}, ROOM_IDX=#{ROOM_IDX}, DOMAIN_NAME=#{DOMAIN_NAME}, CC_DOMAIN=#{CC_DOMAIN}, IP=#{IP}, ATTACK_IP=#{ATTACK_IP}, SYS_RECTIFY_SUGGEST=#{SYS_RECTIFY_SUGGEST}, FINAL_RECTIFY_SUGGES=#{FINAL_RECTIFY_SUGGES}, IS_FORCE_CLOSE=#{IS_FORCE_CLOSE}, IS_WHITE_LIST=#{IS_WHITE_LIST}, FORCE_CLOSE_DESC=#{FORCE_CLOSE_DESC}, RECTIFY_TERM=#{RECTIFY_TERM}, CLOSE_TERM=#{CLOSE_TERM}, SEND_TIME=#{SEND_TIME}, SEND_TIMESTAMP=#{SEND_TIMESTAMP}, RECTIFY_STATE=#{RECTIFY_STATE}, RECTIFY_MEASURE=#{RECTIFY_MEASURE}, FEEDBACK_TIME=#{FEEDBACK_TIME}, FEEDBACK_TIMESTAMP=#{FEEDBACK_TIMESTAMP}, WEB_CASE_RID=#{WEB_CASE_RID}, MAPPING_MODE=#{MAPPING_MODE}, MEMO=#{MEMO}, MODIFIEDTIME=#{MODIFIEDTIME}, RECORDSTATE=#{RECORDSTATE}, SNAPSHOP=#{SNAPSHOP}, KEYWORDS=#{KEYWORDS}, URL=#{URL},
