mapper.select:
select sii.IDC_NAME as TRANS_IDC_ID,ssw.WEBSITE_NAME as TRANS_WEB_CASE_RID, ssp.DETIAL_COUNT as TRANS_RID,case WHEN ssp.SNAPSHOP is not null and ssp.SNAPSHOP!='' then '详情' else '无' end as TRANS_SNAPSHOP, from SMMS_EVENT_MAIN as ssp left join smms_idc_info as sii on ssp.ACCESS_ID=sii.IDC_ID left join SMMS_WEB_CASE as ssw on ssp.WEB_CASE_RID=ssw.RID left join S_USER as use on ssp.ENFORCE_USER=use.USERID
ssp.RECORDSTATE='0'
${whereCondition}
order by ${order}


select count(main.WEB_CASE_RID) as WEBCASETOTAL,max(main.rid) as MAIN_RID ,max(main.DETIAL_COUNT) as DETIAL_COUNT, max(main.RECTIFY_STATE) as RECTIFY_STATE,max(main.CREATTIME) as CREATTIME,max(main.ENFORCE_COUNT) as ENFORCE_COUNT,max(main.THREAT_TYPE4) as THREAT_TYPE4,max(main.KEYWORDS) as KEYWORDS,max(main.IS_FORCE_CLOSE) as IS_FORCE_CLOSE,max(main.PRIORITY) as PRIORITY,max(main.THREAT_LEVEL) as THREAT_LEVEL, 
 max(THREAT_TYPE1) as THREAT_TYPE1,max(THREAT_TYPE2) as THREAT_TYPE2,max(FINAL_RECTIFY_SUGGEST) as FINAL_RECTIFY_SUGGEST from SMMS_EVENT_MAIN as main 
where main.RECORDSTATE='0' and  main.WEB_CASE_RID=#{WEB_CASE_RID}


select count(main.DOMAIN_NAME) as DOMAINTOTAL,max(main.rid) as MAIN_RID,max(main.DETIAL_COUNT) as DETIAL_COUNT, max(main.RECTIFY_STATE) as RECTIFY_STATE,max(main.CREATTIME) as CREATTIME,max(main.ENFORCE_COUNT) as ENFORCE_COUNT,max(main.THREAT_TYPE4) as THREAT_TYPE4,max(main.KEYWORDS) as KEYWORDS,max(main.IS_FORCE_CLOSE) as IS_FORCE_CLOSE,max(main.PRIORITY) as PRIORITY,max(main.THREAT_LEVEL) as THREAT_LEVEL ,
   max(THREAT_TYPE1) as THREAT_TYPE1,max(THREAT_TYPE2) as THREAT_TYPE2,max(FINAL_RECTIFY_SUGGEST) as FINAL_RECTIFY_SUGGEST   from SMMS_EVENT_MAIN as main 
where main.RECORDSTATE='0' and main.DOMAIN_NAME=#{DOMAIN_NAME}


select count(main.IP) as IPTOTAL,max(main.rid) as MAIN_RID,max(main.DETIAL_COUNT) as DETIAL_COUNT, max(main.RECTIFY_STATE) as RECTIFY_STATE,max(main.CREATTIME) as CREATTIME,max(main.ENFORCE_COUNT) as ENFORCE_COUNT,max(main.THREAT_TYPE4) as THREAT_TYPE4,max(main.KEYWORDS) as KEYWORDS ,max(main.IS_FORCE_CLOSE) as IS_FORCE_CLOSE,max(main.PRIORITY) as PRIORITY,max(main.THREAT_LEVEL) as THREAT_LEVEL ,
 max(THREAT_TYPE1) as THREAT_TYPE1,max(THREAT_TYPE2) as THREAT_TYPE2,max(FINAL_RECTIFY_SUGGEST) as FINAL_RECTIFY_SUGGEST	from SMMS_EVENT_MAIN as main 
where main.RECORDSTATE='0' and main.IP=#{IP}

mapper.update:
update SMMS_EVENT_MAIN set 
DETIAL_COUNT=#{DETIAL_COUNT},
MODIFIEDTIME=#{MODIFIEDTIME}
where RID=#{RID}

update SMMS_EVENT_MAIN
IS_FORCE_CLOSE=#{IS_FORCE_CLOSE}, PRIORITY=#{PRIORITY}, THREAT_LEVEL=#{THREAT_LEVEL}, MODIFIEDTIME=#{MODIFIEDTIME}, RECTIFY_STATE=#{RECTIFY_STATE}, FORCE_CLOSE_DESC=#{FORCE_CLOSE_DESC}
where RID=#{RID}

update SMMS_EVENT_MAIN set 
DETIAL_COUNT=DETIAL_COUNT+1
where RID=#{RID}

update SMMS_EVENT_MAIN
THREAT_TYPE4=#{THREAT_TYPE4}, KEYWORDS=#{KEYWORDS}, THREAT_LEVEL=#{THREAT_LEVEL}, IS_FORCE_CLOSE=#{IS_FORCE_CLOSE}, MODIFIEDTIME=#{MODIFIEDTIME}, RECTIFY_STATE=#{RECTIFY_STATE}, FORCE_CLOSE_DESC=#{FORCE_CLOSE_DESC}
where RID=#{RID}

