mapper.select:
select ssp.CLIENTNAME as clientname, ssp.REPORT_CITY as report_city, ssp.REPORT_TIME as report_time, ssp.OCCUR_TIME as occur_time, ssp.EVENT_TYPE1 as event_type1, ssp.IP as ip, ssp.DOMAIN_NAME as domain_name, ssp.URL as url, ssp.ACCESS_ID as access_id, ssp.ROOM_NAME as room_name, ssp.ROOM_IDX as room_idx, ssp.LOG_RID as log_rid, ssp.LOG_TABLENAME as log_tablename, ssp.LOG_FILENAME as log_filename, ssp.MODIFIEDTIME as modifiedtime, ssw.WEBSITE_NAME as web_case_name, crc1.CRC_VALUE as event_type2, crc2.CRC_VALUE as event_type3 from SMMS_PENDING_EVENT as ssp
left join SMMS_WEB_CASE as ssw on ssp.WEB_CASE_RID=ssw.RID left join smms_crc_mapting as crc1 on ssp.EVENT_TYPE2 = crc1.CRC_KEY left join smms_crc_mapting as crc2 on ssp.EVENT_TYPE3 = crc2.CRC_KEY
ssp.recordstate ='0' and ssp.ip=#{IP} and ssp.access_id=#{ACCESS_ID} and ssp.WEB_CASE_RID is not null and ssp.WEB_CASE_RID != '' and ssp.log_tablename != 'SMMS_AF_LOG' and (crc1.CRC_TYPE='a' or crc1.CRC_TYPE ='r' or crc1.CRC_TYPE ='t') and (crc2.CRC_TYPE='a' or crc2.CRC_TYPE ='r' or crc2.CRC_TYPE ='t') and ssp.OCCUR_TIME >= #{STARTTIME} and ssp.OCCUR_TIME <= #{ENDTIME} ORDER BY ssp.OCCUR_TIME DESC LIMIT #{LIMIT} OFFSET #{OFFSET}

select count(*) from SMMS_PENDING_EVENT as ssp
left join SMMS_WEB_CASE as ssw on ssp.WEB_CASE_RID=ssw.RID left join smms_crc_mapting as crc1 on ssp.EVENT_TYPE2 = crc1.CRC_KEY left join smms_crc_mapting as crc2 on ssp.EVENT_TYPE3 = crc2.CRC_KEY
ssp.recordstate ='0' and ssp.ip=#{IP} and ssp.access_id=#{ACCESS_ID} and ssp.WEB_CASE_RID is not null and ssp.WEB_CASE_RID != '' and ssp.log_tablename != 'SMMS_AF_LOG' and (crc1.CRC_TYPE='a' or crc1.CRC_TYPE ='r' or crc1.CRC_TYPE ='t') and (crc2.CRC_TYPE='a' or crc2.CRC_TYPE ='r' or crc2.CRC_TYPE ='t') and ssp.OCCUR_TIME >= #{STARTTIME} and ssp.OCCUR_TIME <= #{ENDTIME}

select ssp.CLIENTNAME as clientname, ssp.REPORT_CITY as report_city, ssp.REPORT_TIME as report_time, ssp.OCCUR_TIME as occur_time, ssp.EVENT_TYPE1 as event_type1, ssp.IP as ip, ssp.DOMAIN_NAME as domain_name, ssp.URL as url, ssp.ACCESS_ID as access_id, ssp.ROOM_NAME as room_name, ssp.ROOM_IDX as room_idx, ssp.LOG_RID as log_rid, ssp.LOG_TABLENAME as log_tablename, ssp.LOG_FILENAME as log_filename, ssp.MODIFIEDTIME as modifiedtime, ssw.WEBSITE_NAME as web_case_name, crc1.CRC_VALUE as event_type2, crc2.CRC_VALUE as event_type3 from SMMS_PENDING_EVENT as ssp
left join SMMS_WEB_CASE as ssw on ssp.WEB_CASE_RID=ssw.RID left join smms_crc_mapting as crc1 on ssp.EVENT_TYPE2 = crc1.CRC_KEY left join smms_crc_mapting as crc2 on ssp.EVENT_TYPE3 = crc2.CRC_KEY
ssp.recordstate ='0' and ssp.ip=#{IP} and ssp.access_id=#{ACCESS_ID} and (ssp.WEB_CASE_RID is null or ssp.WEB_CASE_RID = '') and ssp.log_tablename != 'SMMS_AF_LOG' and (crc1.CRC_TYPE='a' or crc1.CRC_TYPE ='r' or crc1.CRC_TYPE ='t') and (crc2.CRC_TYPE='a' or crc2.CRC_TYPE ='r' or crc2.CRC_TYPE ='t') and ssp.OCCUR_TIME >= #{STARTTIME} and ssp.OCCUR_TIME <= #{ENDTIME} ORDER BY ssp.OCCUR_TIME DESC LIMIT #{LIMIT} OFFSET #{OFFSET}

select count(*) from SMMS_PENDING_EVENT as ssp
left join SMMS_WEB_CASE as ssw on ssp.WEB_CASE_RID=ssw.RID left join smms_crc_mapting as crc1 on ssp.EVENT_TYPE2 = crc1.CRC_KEY left join smms_crc_mapting as crc2 on ssp.EVENT_TYPE3 = crc2.CRC_KEY
ssp.recordstate ='0' and ssp.ip=#{IP} and ssp.access_id=#{ACCESS_ID} and (ssp.WEB_CASE_RID is null or ssp.WEB_CASE_RID = '') and ssp.log_tablename != 'SMMS_AF_LOG' and (crc1.CRC_TYPE='a' or crc1.CRC_TYPE ='r' or crc1.CRC_TYPE ='t') and (crc2.CRC_TYPE='a' or crc2.CRC_TYPE ='r' or crc2.CRC_TYPE ='t') and ssp.OCCUR_TIME >= #{STARTTIME} and ssp.OCCUR_TIME <= #{ENDTIME}

mapper.update:
update SMMS_EVENT_MAIN
EVENT_SOURCE=#{EVENT_SOURCE}, CLIENTNAME=#{CLIENTNAME}, REPORT_CITY=#{REPORT_CITY}, EVENT_TYPE1=#{EVENT_TYPE1}, THREAT_LEVEL=#{THREAT_LEVEL}, THREAT_NAME=#{THREAT_NAME}, THREAT_TYPE1=#{THREAT_TYPE1}, THREAT_TYPE2=#{THREAT_TYPE2}, THREAT_TYPE3=#{THREAT_TYPE3}, THREAT_TYPE4=#{THREAT_TYPE4}, ACCESS_ID=#{ACCESS_ID}, ROOM_NAME=#{ROOM_NAME}, ROOM_IDX=#{ROOM_IDX}, DOMAIN_NAME=#{DOMAIN_NAME}, CC_DOMAIN=#{CC_DOMAIN}, IP=#{IP}, ATTACK_IP=#{ATTACK_IP}, SYS_RECTIFY_SUGGEST=#{SYS_RECTIFY_SUGGEST}, FINAL_RECTIFY_SUGGEST=#{FINAL_RECTIFY_SUGGEST}, IS_FORCE_CLOSE=#{IS_FORCE_CLOSE}, IS_WHITE_LIST=#{IS_WHITE_LIST}, FORCE_CLOSE_DESC=#{FORCE_CLOSE_DESC}, RECTIFY_TERM=#{RECTIFY_TERM}, CLOSE_TERM=#{CLOSE_TERM}, SEND_TIME=#{SEND_TIME}, SEND_TIMESTAMP=#{SEND_TIMESTAMP}, RECTIFY_STATE=#{RECTIFY_STATE}, RECTIFY_MEASURE=#{RECTIFY_MEASURE}, FEEDBACK_TIME=#{FEEDBACK_TIME}, FEEDBACK_TIMESTAMP=#{FEEDBACK_TIMESTAMP}, WEB_CASE_RID=#{WEB_CASE_RID}, MAPPING_MODE=#{MAPPING_MODE}, MEMO=#{MEMO}, MODIFIEDTIME=#{MODIFIEDTIME}, RECORDSTATE=#{RECORDSTATE}, SNAPSHOP=#{SNAPSHOP}, KEYWORDS=#{KEYWORDS}, DETIAL_COUNT=#{DETIAL_COUNT}, ENFORCE_COUNT=#{ENFORCE_COUNT}, ENFORCE_TIME=#{ENFORCE_TIME}, ENFORCE_USER=#{ENFORCE_USER}, RECTIFY_COUNT=#{RECTIFY_COUNT}, PRIORITY=#{PRIORITY}, DETAIL_RID=#{DETAIL_RID}, URL=#{URL},
where RID=#{RID}
