mapper.select:
select sii.IDC_NAME as TRANS_IDC_ID,ssw.WEBSITE_NAME as TRANS_WEB_CASE_RID, ssp.DETIAL_COUNT as TRANS_RID,a.OPEN_APP_TIME,a.CHECK_RESULT,a.APP_RESULT, from SMMS_EVENT_MAIN as ssp left join smms_idc_info as sii on ssp.ACCESS_ID=sii.IDC_ID left join SMMS_WEB_CASE as ssw on ssp.WEB_CASE_RID=ssw.RID left join SMMS_WEB_OPEN_APP as a on ssp.RID = a.EVENT_RID
a.RECORDSTATE='0'
${whereCondition}
order by ${order}

select count(*) NUM from SMMS_EVENT_MAIN as ssp left join smms_idc_info as sii on ssp.ACCESS_ID=sii.IDC_ID left join SMMS_WEB_CASE as ssw on ssp.WEB_CASE_RID=ssw.RID left join SMMS_WEB_OPEN_APP as a on ssp.RID = a.EVENT_RID
a.RECORDSTATE='0'
${whereCondition}

mapper.update:
update SMMS_WEB_OPEN_APP
EVENT_RID=#{EVENT_RID}, ACCESS_ID=#{ACCESS_ID}, CHECK_RESULT=#{CHECK_RESULT}, OPEN_APP_TIME=#{OPEN_APP_TIME}, RECTIFY_MEASURE=#{RECTIFY_MEASURE}, RECTIFY_RESULT=#{RECTIFY_RESULT}, RESTORE_REASON=#{RESTORE_REASON}, APP_LEVEL=#{APP_LEVEL}, IS_ACCEPT=#{IS_ACCEPT}, REJECT_REASON=#{REJECT_REASON}, APP_USERID=#{APP_USERID}, APP_TIME=#{APP_TIME}, APP_OPINION=#{APP_OPINION}, APP_RESULT=#{APP_RESULT}, ISSUE_STATE=#{ISSUE_STATE}, CHECK_RESULT=#{CHECK_RESULT}, OPEN_APP_TIME=#{OPEN_APP_TIME}, SEND_TIME=#{SEND_TIME}, RECORDSTATE=#{RECORDSTATE},
where RID=#{RID}
